statFolder = "ass3";passengers = [10, 50, 100, 150, 200];stdDev = [];mean = [];for i = 1:length(passengers)    A = load([statFolder "/stat_" num2str(passengers(i)) ".txt"]);    disp(["Standardavvikelse " num2str(passengers(i))]);  tmpDev = sqrt(var(A))  stdDev = [stdDev tmpDev];    disp(["MedelvÃ¤rde " num2str(passengers(i))]);  tmpMean = mean(A)  mean = [mean tmpMean];endplot(passengers,stdDev)plot(passengers,mean)