statFolder = "ass3";passengers = [10, 50, 100, 150, 200];stdDev = [];myMean = [];for i = 1:length(passengers)    A = load([statFolder "/stat_" num2str(passengers(i)) ".txt"]);    disp(["Standard deviation " num2str(passengers(i))]);  tmpDev = sqrt(var(A))  stdDev = [stdDev tmpDev];    disp(["Mean " num2str(passengers(i))]);  tmpMean = mean(A)  myMean = [myMean tmpMean];  disp("");endfigure 1plot(passengers,stdDev)title("Standard deviations");xlabel("Passengers (nr)");ylabel("StdDev");figure 2plot(passengers,myMean)title("Average lift travel times");xlabel("Passengers (nr)");ylabel("Avg travel times");